mapper.select:
select * from resource_trade_mark

  and delete_flag!='1'

    and agreement_id = #{agreementId,jdbcType=VARCHAR}


    and adviser = #{adviser,jdbcType=VARCHAR}


order by create_date ASC

  limit #{offset}, #{limit}

select count(1)
from resource_trade_mark

  and delete_flag!='1'

    and agreement_id = #{agreementId,jdbcType=VARCHAR}


    and adviser = #{adviser,jdbcType=VARCHAR}
